import{g as d}from"./getAssetsFile.a139323f.js";import{a as i,an as n,o as r,b as _,e as a,_ as s,U as m,Y as e,u as c,aI as v,aF as b,aG as h}from"./vue.dd6c0a93.js";import{a as f}from"./index.39131854.js";const l=t=>(b("data-v-4a8a9a4a"),t=t(),h(),t),u={class:"resource"},g={class:"wrap"},w={class:"page-nav"},I=l(()=>a("span",null,"详情",-1)),x={class:"resource-content"},y={class:"resource-info"},B=l(()=>a("div",{class:"info-img"},[a("img",{src:"https://www.kczg.org.cn/download/decrypt?key=aHR0cHM6Ly9pbWctei5rY3pnLm9yZy5jbi9mcnVpdC9lbmNyeXB0VXBsb2FkLzIwMjQvMDQvMTcvMTAvZGJmNmUwLmpwZw%3D%3D",alt:""})],-1)),k={class:"info-content"},N=v('<h3 class="info-title" data-v-4a8a9a4a> 生物医药数据大规模采集应用解决方案 </h3><ol data-v-4a8a9a4a><li data-v-4a8a9a4a><span class="item-label" data-v-4a8a9a4a>方案特色：</span><div class="info-tag" data-v-4a8a9a4a><em data-v-4a8a9a4a>生产过程数字化</em><em data-v-4a8a9a4a>科技服务</em><em data-v-4a8a9a4a>医疗</em></div></li><li data-v-4a8a9a4a><span class="item-label" data-v-4a8a9a4a>方案概述：</span><p class="info-desc" data-v-4a8a9a4a> 生物医药行业作为一个高度复杂和创新的新兴领域，面临着诸多发展和经营挑战，例如质量安全问题、产销平衡问题、销售效能问题。这些挑战复杂性高、涉及面广，一度困扰着众多生物医药企业。 </p></li><li data-v-4a8a9a4a><span class="item-label" data-v-4a8a9a4a>方案开发商：</span><p class="info-company" data-v-4a8a9a4a> 中国移动通信集团天津有限公司 </p></li><li data-v-4a8a9a4a><span class="item-label" data-v-4a8a9a4a>星级评定：</span><div class="item-rate" data-v-4a8a9a4a><span data-v-4a8a9a4a></span><span data-v-4a8a9a4a></span><span data-v-4a8a9a4a></span><span data-v-4a8a9a4a></span></div></li></ol>',2),V={class:"info-meta"},L={class:"meta-time"},M={class:"meta-view"},z=v('<div class="info-box" data-v-4a8a9a4a><span data-v-4a8a9a4a>方案描述</span><p data-v-4a8a9a4a> 生物医药行业作为一个高度复杂和创新的新兴领域，面临着诸多发展和经营挑战，例如质量安全问题、产销平衡问题、销售效能问题。这些挑战复杂性高、涉及面广，一度困扰着众多生物医药企业。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> 针对这些挑战，帆软软件利用商业智能(BI)技术，为生物医药企业提供有效的数据应用解决方案。对于质量安全问题，帆软软件打通生产、仓储和运输数据，实现生产全过程透明、库存&amp;运输全链路追踪的效果；对于产销平衡问题，帆软软件建立从计划层到执行层一整套滚动运算机制，借助权限体系和数据共享机制，实现整个供应链业务域各角色的信息透明、提升供应链协同效率；对于销售效能问题，帆软建立事前市场洞察-事中过程管控-事后业绩复盘的销售闭环管理机制，全方位提升销售效能。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> 通过帆软软件《生物医药行业数据应用解决方案》的落地实践，上海莱士血液、北京科兴生物、云南沃森生物等几十家生物医药龙头企业实现了企业级的降本增效提质，切实地“让数据成为生产力”！ </p><span data-v-4a8a9a4a>方案特色</span><p data-v-4a8a9a4a> 帆软软件医药事业部根据生物医药的行业政策以及特征趋势，量身定制打造行业数据应用解决方案。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> （一）特色1：“批签发”进度透明，减少产销信息差 <br data-v-4a8a9a4a> 生物医药行业的批签发制度规定，每批制品出厂上市或者进口时需要进行强制性检验、审核；检验不合格或者审核不被批准者，不得上市或者进口。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> 本方案打造了整套批签发进度看板体系，可以帮助生物医药企业减少产销端的信息差，根据批签发情况及时调整生产策略、促进产销平衡。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> （二）特色2：面向药监局“线上飞检”，打造飞检数据平台 <br data-v-4a8a9a4a> 飞检的全称为飞行检查，是指事先不通知被检查部门实施的现场检查的监管检查方式。自2017年长生生物“毒疫苗”事件后，生物医药一直是重点监管行业。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> 面向药监局“线上飞检”，本方案积极就各项政策要求积极响应，打造服务于“企业”与“药监局”两端的飞检数据平台。 <br data-v-4a8a9a4a> （三）特色3：疫苗 “一票制”政策，定制营销分析方案 <br data-v-4a8a9a4a> 2017年事件后，疫苗行业进入“一票制时代”，一类苗和二类苗都必须由生产企业直接向省疾控供货。自此疫苗企业的营销业务中各类生态角色及其定位都发生了变化。 <br data-v-4a8a9a4a><br data-v-4a8a9a4a> 本方案提炼出疫苗行业营销环节业务核心管控点，为疫苗企业定制打造了整套的营销闭环分析链路,涵盖“事前市场洞察”、“事中过程管控”、“事后业绩复盘”的分析内容。 </p></div>',1),A=i({name:"expertIndex"}),C=i({...A,setup(t){return(S,D)=>{const p=n("router-link"),o=n("svgIcon");return r(),_("div",u,[a("div",g,[a("div",w,[s(p,{to:"/case"},{default:m(()=>[e("标杆案例")]),_:1}),I]),a("div",x,[a("div",y,[B,a("div",k,[N,a("div",V,[a("p",L,[s(o,{class:"icon-bg",color:"#98A3B7",size:14,name:c(d)("icon/icon-time.svg")},null,8,["name"]),e(" 2024-05-20 ")]),a("p",M,[s(o,{class:"icon-bg",color:"#98A3B7",size:16,name:c(d)("icon/icon-view.svg")},null,8,["name"]),e(" 23 ")])])])]),z])])])}}});const j=f(C,[["__scopeId","data-v-4a8a9a4a"]]);export{j as default};
